mapper.select:
SELECT ExV.*
FROM Layers AS L LEFT JOIN LayerObjects AS O ON L.layer_id = O.layer_id LEFT JOIN ExternalVideo AS ExV ON O.object_id = ExV.object_id
WHERE L.layer_id = #{layerId};

SELECT object_id
FROM LayerObjects AS O
WHERE O.layer_id = #{layerId}
    AND type = #{type}

mapper.insert:

    SELECT LAST_INSERT_ID()


INSERT INTO LayerObjects (layer_id, preset_id, screen_id, type, object_nm)
VALUES (#{layerId}, #{presetId}, #{screenId}, #{type}, #{objectNm});

INSERT INTO ExternalVideo (object_id, type, pos_x, pos_y, width, height, ord, rtsp_url, video_nm, video_format)
VALUES (LAST_INSERT_ID(), #{type}, #{posX}, #{posY}, #{width}, #{height}, #{ord}, #{rtspUrl}, #{videoNm}, #{videoFormat});

mapper.update:

    SELECT ex_video_id
    FROM ExternalVideo
    WHERE object_id = #{objectId}
    AND type = #{type}

UPDATE ExternalVideo
SET
    pos_x = #{posX},
    pos_y = #{posY},
    width = #{width},
    height = #{height},
    ord = #{ord},
    rtsp_url = #{rtspUrl},
    video_nm = #{videoNm},
    video_format = #{videoFormat}
WHERE object_id = #{objectId}
    AND type = #{type}

mapper.select:
SELECT object_id
FROM LayerObjects AS O
WHERE O.layer_id = #{layerId}
  AND type = #{type}

mapper.insert:

    SELECT LAST_INSERT_ID()


INSERT INTO LayerObjects (layer_id, preset_id, screen_id, type, object_nm)
VALUES (#{layerId}, #{presetId}, #{screenId}, #{type}, #{objectNm});

INSERT INTO External_Info (object_id, type, pos_x, pos_y, width, height, ord
, image_list, font_nm, font_size, bold, italic, underline, forecolor, image_layout)
VALUES (LAST_INSERT_ID(), #{type}, #{posX}, #{posY}, #{width}, #{height}, #{ord}
, #{imageList}, #{fontNm}, #{fontSize}, #{bold}, #{italic}, #{underline}, #{forecolor}, #{imageLayout});

mapper.update:
SELECT ex_info_id FROM External_Info WHERE object_id = #{objectId} AND type = #{type}
UPDATE External_Info SET pos_x = #{posX}, pos_y = #{posY}, width = #{width}, height = #{height}, ord = #{ord}, image_list = #{imageList}, font_nm = #{fontNm}, font_size = #{fontSize}, bold = #{bold}, italic = #{italic}, underline = #{underline}, forecolor = #{forecolor}, image_layout = #{imageLayout}
WHERE object_id = #{objectId} AND type = #{type}
