mapper.select:
SELECT count(preset_id)
FROM Presets
WHERE preset_nm = #{presetNm}
    AND rowsize = #{rowsize}
    AND columnsize = #{columnsize}

SELECT COUNT(preset_id)
FROM Presets
WHERE Presets.preset_id = #{presetId}

mapper.update:
DELETE P
FROM Presets AS P
WHERE P.preset_id = #{presetId}

mapper.select:
SELECT COUNT(preset_id)
FROM Presets
WHERE Presets.preset_id = #{presetId}

mapper.update:
UPDATE Presets
SET preset_nm = #{presetNm}
WHERE Presets.preset_id = #{presetId}.
